    Canon:SetVisible(A)
    Canon:registerEvent(GUIEvent.ButtonClick, function()
        local camera = SceneManager.Instance():getMainCamera()
        local direction = camera:getDirection()
        
        local magnitude = math.sqrt(direction.x^2 + direction.y^2 + direction.z^2)
        direction.x = direction.x / magnitude
        direction.y = direction.y / magnitude
        direction.z = direction.z / magnitude
        local speed = 4
        local velocity = VectorUtil.newVector3(direction.x * speed, direction.y * speed, direction.z * speed)

        local player = PlayerManager:getClientPlayer().Player
        player:setVelocity(velocity)
        SoundUtil.playSound(313)
    end)

    A = not A
    Canon:SetVisible(A)
end

