function GMHelper:SpawnBlockToword()
    GMHelper:openInput({ "","","" }, function(martin,long,r)
    local yaw = PlayerManager:getClientPlayer().Player.rotationYaw
      local directions = {"东", "东南", "南", "西南", "西", "西北", "北", "东北"}
      
      -- 映射偏航角到0-360度范围
      yaw = yaw % 360
      if yaw < 0 then
          yaw = yaw + 360
      end
      
